-- Hockey_DB_Script.sql



DROP TABLE Player CASCADE CONSTRAINTS;

CREATE TABLE Player (
  Name VARCHAR2(30) PRIMARY KEY,
  Position CHAR(1),
  Birthdate DATE,
  Birthplace VARCHAR2(30),
  Seasons CHAR(9),
  GamesPlayed NUMBER(4),
  Goals NUMBER(4),
  Assists NUMBER(4),
  Points NUMBER(4),
  Penalty NUMBER(4),
  Years NUMBER(2)
);

